const T=g=>{const t=new Date(g),o=new Date,i=Date.UTC(t.getUTCFullYear(),t.getUTCMonth(),t.getUTCDate(),t.getUTCHours(),t.getUTCMinutes(),t.getUTCSeconds()),c=Date.UTC(o.getUTCFullYear(),o.getUTCMonth(),o.getUTCDate(),o.getUTCHours(),o.getUTCMinutes(),o.getUTCSeconds()),f=Math.floor((c-i)/1e3);if(f<60)return"Just now";const n=Math.floor(f/60);if(n<60)return`${n} minute${n>1?"s":""} ago`;const s=Math.floor(n/60);if(s<24)return`${s} hour${s>1?"s":""} ago`;const e=Math.floor(s/24);if(e<7)return`${e} day${e>1?"s":""} ago`;const r=Math.floor(e/7);if(r<4)return`${r} week${r>1?"s":""} ago`;const a=Math.floor(e/30);if(a<12)return`${a} month${a>1?"s":""} ago`;const u=Math.floor(e/365);return`${u} year${u>1?"s":""} ago`};export{T as f};
